import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams, Link } from "react-router-dom";

function ProductDetails() {
  const { id } = useParams();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    axios.get(`https://fakestoreapi.com/products/${id}`).then((res) => {
      setProduct(res.data);
    });
  }, [id]);

  if (!product) return <p>Loading...</p>;

  return (
    <div className="p-8 flex flex-col items-center">
      <img src={product.image} alt={product.title} className="w-60" />
      <h2 className="text-2xl font-semibold mt-4">{product.title}</h2>
      <p className="text-gray-700 mt-2">{product.description}</p>
      <p className="text-xl font-bold mt-4">â‚¹{product.price}</p>

      {/* ðŸ”¥ Buy Now Button */}
      <Link to="/payment">
        <button className="bg-blue-600 text-white px-6 py-2 rounded-lg mt-5">
          Buy Now
        </button>
      </Link>
    </div>
  );
}

export default ProductDetails;
