import { useContext } from "react";
import { CartContext } from "../context/CartContext";

const Cart = () => {
  const { cart, removeFromCart } = useContext(CartContext);

  return (
    <div className="max-w-5xl mx-auto px-6 py-10">
      <h2 className="text-3xl font-bold mb-6">Your Cart</h2>

      {cart.length === 0 ? (
        <p className="text-gray-600 text-xl">Your cart is empty.</p>
      ) : (
        cart.map((item) => (
          <div
            key={item.id}
            className="bg-white shadow-lg p-4 rounded-xl flex items-center gap-6 mb-6"
          >
            <img src={item.image} alt="" className="h-24 w-24 object-contain" />

            <div className="flex-1">
              <h3 className="text-xl font-semibold">{item.title}</h3>
              <p className="text-blue-600 font-bold text-lg">â‚¹ {item.price}</p>
            </div>

            <button
              onClick={() => removeFromCart(item.id)}
              className="bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600 transition"
            >
              Remove
            </button>
          </div>
        ))
      )}
    </div>
  );
};

export default Cart;
