import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import { useParams, Link } from "react-router-dom";
import { CartContext } from "../context/CartContext";

function ProductDetails() {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const { addToCart } = useContext(CartContext);

  useEffect(() => {
    axios.get(`https://fakestoreapi.com/products/${id}`).then((res) => {
      setProduct(res.data);
    });
  }, [id]);

  if (!product) return <p className="text-center mt-10">Loading...</p>;

  return (
    <div className="p-8 flex flex-col items-center">
      <img src={product.image} alt={product.title} className="w-60" />

      <h2 className="text-2xl font-semibold mt-4">{product.title}</h2>

      <p className="text-gray-700 mt-2 max-w-xl text-center">
        {product.description}
      </p>

      <p className="text-xl font-bold mt-4">â‚¹{product.price}</p>

      {/* ðŸ”¥ Buttons Section */}
      <div className="flex gap-4 mt-6">

        {/* Add to Cart Button */}
        <button
          onClick={() => addToCart(product)}
          className="bg-yellow-500 hover:bg-yellow-600 text-white px-6 py-2 rounded-lg shadow"
        >
          Add to Cart
        </button>

        {/* Buy Now Button â†’ goes to payment page */}
        <Link to="/payment">
          <button className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-2 rounded-lg shadow">
            Buy Now
          </button>
        </Link>

      </div>
    </div>
  );
}

export default ProductDetails;
