import React, { useState } from "react";

export default function PaymentCard() {
  const [open, setOpen] = useState(false);
  const [paymentValue, setPaymentValue] = useState("");

  return (
    <div className="flex flex-col items-center mt-10">
      {/* Buy Button */}
      <button 
        onClick={() => setOpen(!open)}
        className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-3 rounded-lg shadow-lg transition"
      >
        Buy Now
      </button>

      {/* Payment Input */}
      {open && (
        <div className="mt-5 w-80 bg-white p-5 rounded-xl shadow-xl border">
          <h2 className="text-xl font-semibold mb-3">Enter Payment Details</h2>

          <input
            type="text"
            placeholder="Enter UPI / Phone / Card Number"
            value={paymentValue}
            onChange={(e) => setPaymentValue(e.target.value)}
            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
          />

          <button
            onClick={() => alert("Payment Done: " + paymentValue)}
            className="mt-4 w-full bg-green-600 hover:bg-green-700 text-white py-2 rounded-lg transition"
          >
            Pay
          </button>
        </div>
      )}
    </div>
  );
}
